mapper.select:

	SELECT * 
	  FROM(
	  		SELECT boardno, boardwriter, boardpwd, boardtitle, boardcontent,boarddate, rownum rnum
	   	   	  FROM(
	   	   	  		SELECT * 
	   	   	  		  FROM QuestionBoard 
	   	   	  		  where kind=#{kind}
	   	   	  	  ORDER BY boardno DESC
	   	   	  	  )
			)   
	 WHERE rnum >= #{start} AND rnum <= #{end}
