mapper.select:

	SELECT * 
	  FROM(
	  		SELECT boardno, boardwriter, boardpwd, boardtitle, boardcontent,boarddate, rownum rnum
	   	   	  FROM(
	   	   	  		SELECT * 
	   	   	  		  FROM QuestionBoard 
	   	   	  		  where kind=#{kind}
	   	   	  	  ORDER BY boardno DESC
	   	   	  	  )
			)   
	 WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM( SELECT hospitalno, doctorno, hospitalname, hospitaladdr, hospitalphone,hospitalinstruction,hospitalimage,mon,tues,wed,thurs ,fri,sat,sun,holi,hospitalkind,hospitalstate, rownum rnum FROM( SELECT * FROM hospital
hospitalstate = #{state}
hospitalname LIKE '%'||#{select}||'%'
hospitaladdr LIKE '%'||#{select}||'%'
) )
rnum >= #{start} AND rnum <= #{end}

SELECT count(*) FROM
drug
(
SELECT * FROM (
SELECT * FROM (
