WHERE drugCompany LIKE '%'||#{drugCompany}||'%'
) WHERE drugName LIKE '%'||#{drugName}||'%'
) WHERE
splitlineB LIKE '%'||#{item}||'%'
) WHERE
splitlineF LIKE '%'||#{item}||'%'
) WHERE
colorF LIKE '%'||#{item}||'%'
OR
colorB LIKE '%'||#{item}||'%'
) WHERE drugForm LIKE #{drugForm_etc}
) WHERE
drugForm LIKE '%'||#{item}||'%'
) WHERE shape LIKE '%'||#{shape_etc}||'%'
) WHERE
shape LIKE '%'||#{item}||'%'
)
signF LIKE '%'||#{sign}||'%' OR signB LIKE '%'||#{sign}||'%'
signF LIKE #{sign} OR signB LIKE #{sign}
signF LIKE #{sign}||'%' OR signB LIKE #{sign}||'%'
)

SELECT * FROM ( SELECT drugCode,drugName,drugCompany,drugGroupCode,drugGroup,pro_Usual,admitDate, temperState,shape,drugImage,signF,signB,drugForm,colorF,colorB,splitlineF,splitlineB,maxAxis, minAxis,thickness,registDate,markF,markB,markImageF,markImageB,markCodeF,markCodeB,drugStorageMethod, drugEfficacy,drugUsedCapacity,drugPrecautions, rownum rNum FROM (
SELECT * FROM (
