SELECT * FROM (
SELECT * FROM (
SELECT * FROM (
SELECT * FROM (
SELECT * FROM (
SELECT * FROM (
SELECT * FROM (
SELECT * FROM drug
WHERE drugCompany LIKE '%'||#{drugCompany}||'%'
) WHERE drugName LIKE '%'||#{drugName}||'%'
) WHERE
splitlineB LIKE '%'||#{item}||'%'
