  $(document).ready(function() {
    $("ul.navbar-nav mr-auto > li").on('mouseover', function(){
        $(this).find('a').addClass('hover');
        $(this).find('ol.list-unstyled mx-4 dark-grey-text').show();
    }).on('mouseout',function(){
        $(this).find('a').removeClass('hover');
        $(this).find('ol.list-unstyled mx-4 dark-grey-text').hide();
    });
});




