mapper.select:
			
		select * 
    	  from (
                 SELECT guestNo 
                      , guestid
                      , guestname
                      , address2
                      , guestage
                      , guesttel
                      , sanctions
                      , guestdate
					  , rownum rNum        
            	   from (
                	      select * 
                            from guest
                           where status = 1
                           order by guestNo desc
                        )
               )
               
         WHERE rNum >= #{start}
           AND rNum <= #{end}

mapper.insert:
insert into drug ( drugCode		
				 , drugName
				 , drugCompany
				 , drugGroupCode
				 , drugGroup
				 , pro_Usual
				 , admitDate
				 , temperState
				 , shape	
				 , drugImage	
				 , signF		
				 , signB		
				 , drugForm	
				 , colorF	
				 , colorB	
				 , splitlineF
				 , splitlineB	
				 , maxAxis		
				 , minAxis		
				 , thickness	
				 , registDate
				 , markF		
				 , markB		
				 , markImageF	
				 , markImageB	
				 , markCodeF	
				 , markCodeB	
				 , drugStorageMethod
				 , drugEfficacy		
				 , drugUsedCapacity	
				 , drugPrecautions	
				 )
values(			   drugCode.nextval
	 			 , #{drugName}
	 			 , #{drugCompany}
	 			 , #{drugGroupCode}
				 , #{drugGroup}
				 , #{pro_Usual}
				 , #{admitDate}
				 , #{temperState}
				 , #{shape}
				 , #{drugImage}
				 , #{signF}
				 , #{signB}
				 , #{drugForm}
				 , #{colorF}
				 , #{colorB}
				 , #{splitlineF}
				 , #{splitlineB}
				 , #{maxAxis}
				 , #{minAxis}
				 , #{thickness}
				 , #{registDate}
				 , #{markF}
				 , #{markB}
				 , #{markImageF}
				 , #{markImageB}
				 , #{markCodeF}
				 , #{markCodeB}
				 , #{drugStorageMethod}
				 , #{drugEfficacy}
				 , #{drugUsedCapacity}
				 , #{drugPrecautions}
	  )

mapper.update:
UPDATE drug SET    drugName				=	#{drugName}
				 , drugCompany			=	#{drugCompany}
				 , drugGroupCode		=	#{drugGroupCode}
				 , pro_Usual			=	#{pro_Usual}
				 , drugStorageMethod	=	#{drugStorageMethod}
				 , drugEfficacy			=	#{drugEfficacy}
				 , drugUsedCapacity		=	#{drugUsedCapacity}
				 , drugPrecautions		=	#{drugPrecautions}
 WHERE drugCode = #{drugCode}

UPDATE disease SET diseaseName		=	#{diseaseName}
				 , diseaseDefine	=	#{diseaseDefine}
				 , diseaseCause		=	#{diseaseCause}
				 , diseaseSymptom	=	#{diseaseSymptom}
				 , diseaseDiagnosis	=	#{diseaseDiagnosis}
				 , diseaseCure		=	#{diseaseCure}
				 , complications	=	#{complications}
				 , prevention		=	#{prevention}
 WHERE diseaseCode = #{diseaseCode}

UPDATE exercise SET exerciseName		=	#{exerciseName}
				  , exerciseImage		=	#{exerciseImage}
				  , exerciseDefine		=	#{exerciseDefine}
				  , exerciseMethod		=	#{exerciseMethod}
				  , exercisePart		=	#{exercisePart}
				  , exerciseCaution		=	#{exerciseCaution}
 WHERE exerciseName = #{exerciseName}

mapper.select:
SELECT preventionCode , preventionKind , countryCode , preventionContent , rownum rNum from prevention
where rNum >= #{start} AND rNum <= #{end}
ORDER BY preventionCode desc

SELECT diseaseName , diseaseCode , rownum rNum from disease d join prevention p on d.deseaseCode = p.deseaseCode
where rNum >= #{start} AND rNum <= #{end}
ORDER BY exerciseName desc

mapper.update:
UPDATE event SET advertisementTitle=#{advertisementTitle}, advertisementImage=#{advertisementImage},
	advertisementContents=#{advertisementContents},advertisementThumbnail=#{advertisementThumbnail}
	WHERE advertisementNo = #{advertisementNo}
