mapper.select:

	SELECT * 
	  FROM(
	  		SELECT boardno, boardwriter, boardpwd, boardtitle, boardcontent,boarddate, rownum rnum
	   	   	  FROM(
	   	   	  		SELECT * 
	   	   	  		  FROM QuestionBoard 
	   	   	  		  where kind=#{kind}
	   	   	  	  ORDER BY boardno DESC
	   	   	  	  )
			)   
	 WHERE rnum >= #{start} AND rnum <= #{end}

SELECT count(*) FROM drug 

	
		
			
				signF LIKE '%'||#{sign}||'%'
				OR
				signB LIKE '%'||#{sign}||'%'
			
			
				signF LIKE #{sign}
				OR
				signB LIKE #{sign}
			
			
				signF LIKE #{sign}||'%'
				OR
				signB LIKE #{sign}||'%'
			
		
	
	
		AND shape IN
		
			#{item}
		
	
	
		AND shape LIKE #{shape_etc}
	
	
		AND drugForm IN
		
			#{item}
		
	
	
		AND drugForm LIKE #{drugForm_etc}
	
	
		AND colorF IN
		
			#{item}
		
		OR colorB IN
		
			#{item}
			
	
	
		AND splitlineF IN
		
			#{item}
		
	
	
		AND splitlineB IN
		
			#{item}
		
	
	
		AND drugName LIKE '%'||#{drugName}||'%'
	
	
		AND drugCompany LIKE '%'||#{drugCompany}||'%'

SELECT *
  FROM ( 
  	SELECT  drugCode,drugName,drugCompany,drugGroupCode,drugGroup,pro_Usual,admitDate,
  temperState,shape,drugImage,signF,signB,drugForm,colorF,colorB,splitlineF,splitlineB,maxAxis,
   minAxis,thickness,registDate,markF,markB,markImageF,markImageB,markCodeF,markCodeB,drugStorageMethod,
   drugEfficacy,drugUsedCapacity,drugPrecautions, rownum rNum
         FROM ( 
         	SELECT * 
         	 FROM drug
         	 
				
					
						
							signF LIKE '%'||#{sign}||'%'
							OR
							signB LIKE '%'||#{sign}||'%'
						
						
							signF LIKE #{sign}
							OR
							signB LIKE #{sign}
						
						
							signF LIKE #{sign}||'%'
							OR
							signB LIKE #{sign}||'%'
						
					
				
				
					AND shape IN
					
						#{item}
					
				
				
					AND shape LIKE #{shape_etc}
				
				
					AND drugForm IN
					
						#{item}
					
				
				
					AND drugForm LIKE #{drugForm_etc}
				
				
					AND colorF IN
					
						#{item}
					
					OR colorB IN
					
						#{item}
												
				
				
					AND splitlineF IN
					
						#{item}
					
				
				
					AND splitlineB IN
					
						#{item}
					
				
				
					AND drugName LIKE '%'||#{drugName}||'%'
				
				
					AND drugCompany LIKE '%'||#{drugCompany}||'%'
				
			
         	 ORDER BY registDate DESC )
         )
		       
 	WHERE rNum >= #{start} AND rNum <= #{end}
