mapper.update:
update Myhealth 
	set birth1=#{birth1} , birth2=#{birth2} , birth3=#{birth3} , age=#{age} , height=#{height} , weight=#{weight} , gender=#{gender} , bloodtype= #{bloodtype} 
	where guestNo=#{guestNo}

mapper.select:
	select hospitalname, hospitalphone, reservationTime, reservationKind ,reservationNo, clock, minute
		 from Reservation r ,  Hospital h  
		 where   r.hospitalno = h.hospitalno  AND  guestNo = #{guestNo}
